import React from "react";
import Card from "react-bootstrap/Card";
import MyCard from "./MyCard";
import Button from "react-bootstrap/Button";
import { myContent } from "./const";
import "./meetings.css";

class Meetings extends React.Component {
  constructor(props) {
    super(props);
    this.state = { date: new Date() };
  }

  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000);
  }
  componentWillUnmount() {
    clearInterval(this.timer);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }

  deleteMeetings = ex => {
    const filteredMeetings = this.state.myContent.filter((meeting, index) => {
      return index !== ex;
    });
    this.setState({ myContent: filteredMeetings });
    localStorage.setItem("myContent", JSON.stringify(filteredMeetings));
  };

  render() {
    const post = this.props.post;
    return (
      <Card>
        <div style={{ margin: "10px" }}>
          <div className="d-flex justify-content-between align-items-center mb-2">
            <div className="h6 ">MEETINGS</div>
            <Button
              size="sm"
              variant="btn btn-outline-primary"
              className=" btn btn-outline-primary "
            >
              View all
            </Button>
          </div>
          {myContent.map((item, index) => {
            return <MyCard title={item.title} user={item.user}
            deleteMeetings={()=> {
              this.deleteMeetings(index)
            }} />;
          })}
          <div>
            <h4 className="text-center">
              It is {this.state.date.toLocaleTimeString()}{" "}
            </h4>
          </div>
        </div>
      </Card>
    );
  }
}

export default Meetings;
